CE = \"PMID:20485364\", AUTHORS = \"Changeux JP\", TITLE = \"Nicotine addiction and nicotinic receptors: lessons from genetically modified mice.\", JOURNAL = c(\"Nat Rev Neurosci 11:389-401 (2010)\", \"DOI:10.1038/nrn2849\")), list(REFERENCE = \"PMID:17009926\", AUTHORS = \"Dani JA, Bertrand D\", TITLE = \"Nicotinic acetylcholine receptors and nicotinic cholinergic mechanisms of the central nervous system.\", JOURNAL = c(\"Annu Rev Pharmacol Toxicol 47:699-729 (2007)\", \"DOI:10.1146/annurev.pharmtox.47.120505.105214\")), list(REFERENCE = \"PMID:11906697\", AUTHORS = \"Mansvelder HD, Keath JR, McGehee DS\", TITLE = \"Synaptic mechanisms underlie nicotine-induced excitability of brain reward areas.\", JOURNAL = c(\"Neuron 33:905-19 (2002)\", \"DOI:10.1016/S0896-6273(02)00625-6\")), list(REFERENCE = \"PMID:18723034\", AUTHORS = \"O\'Dell LE\", TITLE = \"A psychobiological framework of the substrates that mediate nicotine use during adolescence.\", JOURNAL = c(\"Neuropharmacology 56 Suppl 1:263-78 (2009)\", \"DOI:10.1016/j.neuropharm.2008.07.039\")), list(REFERENCE = \"PMID:20833261\", AUTHORS = \"Ortells MO, Arias HR\", TITLE = \"Neuronal networks of nicotine addiction.\", JOURNAL = c(\"Int J Biochem Cell Biol 42:1931-5 (2010)\", \"DOI:10.1016/j.biocel.2010.08.019\")), list(REFERENCE = \"PMID:16251989\", AUTHORS = \"Dani JA, Harris RA\", TITLE = \"Nicotine addiction and comorbidity with alcohol abuse and mental illness.\", JOURNAL = c(\"Nat Neurosci 8:1465-70 (2005)\", \"DOI:10.1038/nn1580\")), list(REFERENCE = \"PMID:18640919\", AUTHORS = \"Markou A\", TITLE = \"Review. Neurobiology of nicotine dependence.\", JOURNAL = c(\"Philos Trans R Soc Lond B Biol Sci 363:3159-68 (2008)\", \"DOI:10.1098/rstb.2008.0095\")), list(REFERENCE = \"PMID:15165747\", AUTHORS = \"Dajas-Bailador F, Wonnacott S\", TITLE = \"Nicotinic acetylcholine receptors and the regulation of neuronal signalling.\", JOURNAL = c(\"Trends Pharmacol Sci 25:317-24 (2004)\", \"DOI:10.1016/j.tips.2004.04.006\