INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (394, '05034', 'Alcoholism', 'Alcoholism, also called dependence on alcohol (ethanol), is a chronic relapsing disorder that is progressive and has serious detrimental health outcomes. As one of the primary mediators of the rewarding effects of alcohol, dopaminergic ventral tegmental area (VTA) projections to the nucleus accumbens (NAc) have been identified. Acute exposure to alcohol stimulates dopamine release into the NAc, which activates D1 receptors, stimulating PKA signaling and subsequent CREB-mediated gene expression, whereas chronic alcohol exposure leads to an adaptive downregulation of this pathway, in particular of CREB function. The decreased CREB function in the NAc may promote the intake of drugs of abuse to achieve an increase in reward and thus may be involved in the regulation of positive affective states of addiction. PKA signaling also affects NMDA receptor activity and may play an important role in neuroadaptation in response to chronic alcohol exposure.', 'Human Diseases; Substance dependence', 'Alcoholism', 'Alcohol dependence', 'GO: 0045471', 'ko05034', 'list(REFERENCE = \"PMID:19342616\", AUTHORS = \"Spanagel R\", TITLE = \"Alcoholism: a systems approach from molecular physiology to addictive behavior.\", JOURNAL = c(\"Physiol Rev 89:649-705 (2009)\", \"DOI:10.1152/physrev.00013.2008\")), list(REFERENCE = \"PMID:16102840\", AUTHORS = \"Newton PM, Messing RO\", TITLE = \"Intracellular signaling pathways that regulate behavioral responses to ethanol.\", JOURNAL = c(\"Pharmacol Ther 109:227-37 (2006)\", \"DOI:10.1016/j.pharmthera.2005.07.004\")), list(REFERENCE = \"PMID:14729391\", AUTHORS = \"Mailliard WS, Diamond I\", TITLE = \