INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (395, '05100', 'Bacterial invasion of epithelial cells', 'Many pathogenic bacteria can invade phagocytic and non-phagocytic cells and colonize them intracellularly, then become disseminated to other cells. Invasive bacteria induce their own uptake by non-phagocytic host cells (e.g. epithelial cells) using two mechanisms referred to as zipper model and trigger model. Listeria, Staphylococcus, Streptococcus, and Yersinia are examples of bacteria that enter using the zipper model. These bacteria express proteins on their surfaces that interact with cellular receptors, initiating signalling cascades that result in close apposition of the cellular membrane around the entering bacteria. Shigella and Salmonella are the examples of bacteria entering cells using the trigger model. These bacteria use type III secretion systems to inject protein effectors that interact with the actin cytoskeleton.', 'Human Diseases; Infectious disease: bacterial', 'Bacterial invasion of epithelial cells', 'ko05100', 'list(REFERENCE = \"PMID:19296488\", AUTHORS = \"Mostowy S, Cossart P\", TITLE = \"Cytoskeleton rearrangements during Listeria infection: clathrin and septins as new players in the game.\", JOURNAL = c(\"Cell Motil Cytoskeleton 66:816-23 (2009)\", \"DOI:10.1002/cm.20353\")), list(REFERENCE = \"PMID:16406780\", AUTHORS = \"Hauck CR, Ohlsen K\", TITLE = \"Sticky connections: extracellular matrix protein recognition and integrin-mediated cellular invasion by Staphylococcus aureus.\", JOURNAL = c(\"Curr Opin Microbiol 9:5-11 (2006)\", \"DOI:10.1016/j.mib.2005.12.002\")), list(REFERENCE = \"PMID:17298394\", AUTHORS = \"Wang B, Li S, Dedhar S, Cleary PP\", TITLE = \"Paxillin phosphorylation: bifurcation point downstream of integrin-linked kinase (ILK) in streptococcal invasion.\", JOURNAL = c(\"Cell Microbiol 9:1519-28 (2007)\", \"DOI:10.1111/j.1462-5822.2007.00889.x\")), list(REFERENCE = \"PMID:19185531\", AUTHORS = \"Matsumoto H, Young GM\", TITLE = \"Translocated effectors of Yersinia.\", JOURNAL = c(\"Curr Opin Microbiol 12:94-100 (2009)\", \"DOI:10.1016/j.mib.2008.12.005\")), list(REFERENCE = \"PMID:19157959\", AUTHORS = \"McGhie EJ, Brawn LC, Hume PJ, Humphreys D, Koronakis V\", TITLE = \"Salmonella takes control: effector-driven manipulation of the host.\", JOURNAL = c(\"Curr Opin Microbiol 12:117-24 (2009)\", \"DOI:10.1016/j.mib.2008.12.001\")), list(REFERENCE = \"PMID:17719540\", AUTHORS = \"Iwai H, Kim M, Yoshikawa Y, Ashida H, Ogawa M, Fujita Y, Muller D, Kirikae T, Jackson PK, Kotani S, Sasakawa C\", TITLE = \"A bacterial effector targets Mad2L2, an APC inhibitor, to modulate host cell cycling.\", JOURNAL = c(\"Cell 130:611-23 (2007)\", \"DOI:10.1016/j.cell.2007.06.043\"))', 'map05100', 'Bacterial invasion of epithelial cells', 'Many pathogenic bacteria can invade phagocytic and non-phagocytic cells and colonize them intracellularly, then become disseminated to other cells. Invasive bacteria induce their own uptake by non-phagocytic host cells (e.g. epithelial cells) using two mechanisms referred to as zipper model and trigger model. Listeria, Staphylococcus, Streptococcus, and Yersinia are examples of bacteria that enter using the zipper model. These bacteria express proteins on their surfaces that interact with cellular receptors, initiating signalling cascades that result in close apposition of the cellular membrane around the entering bacteria. Shigella and Salmonella are the examples of bacteria entering cells using the trigger model. These bacteria use type III secretion systems to inject protein effectors that interact with the actin cytoskeleton.', 65, 156, 65, 0, 0, 0, 156, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (396, '05110', 'Vibrio cholerae infection', 'c(\"Cholera toxin (CTX) is one of the main virulence factors of Vibrio cholerae. Once secreted, CTX B-chain (CTXB) binds to ganglioside GM1 on the surface of the host\'s cells. After binding takes place, the entire CTX complex is carried from plasma membrane (PM) to endoplasmic reticulum (ER). In the ER, the A-chain (CTXA) is recognized by protein disulfide isomerase (PDI), unfolded, and delivered to the membrane where the membrane-associated ER-oxidase, Ero1, oxidizes PDI to release the CTXA into the protein-conducting channel, Sec61. CTXA is then retro-translocated to the cytosol and induces water and electrolyte secretion by increasing cAMP levels via adenylate cyclase (AC) to exert toxicity.\", \n\"Other than CTX, Vibrio cholerae generates several toxins that are perilous to eukaryotic cells. Zonula occludens toxin (ZOT) causes tight junction disruption through protein kinase C-dependent actin polymerization. RTX toxin (RtxA) causes actin depolymerization by covalently cross-linking actin monomers into dimers, trimers, and higher multimers. Vibrio cholerae cytolysin (VCC) is an important pore-forming toxin. The assembly of VCC anion channels in cells cause vacuolization and lysis.\")', 'Human Diseases; Infectious disease: bacterial', 'Vibrio cholerae infection', 'Vibrio cholerae pathogenicity signature, cholera toxins [PATH:map05110], Vibrio cholerae pathogenicity signature, toxin coregulated pilus [PATH:map05110]', 'Cholera', 'ko05110', 'list(REFERENCE = \"PMID:10942706\", AUTHORS = \"Trucksis M, Conn TL, Wasserman SS, Sears CL.\", TITLE = \"Vibrio cholerae ACE stimulates Ca(2+)-dependent Cl(-)/HCO(3)(-) secretion in T84 cells in vitro.\", JOURNAL = c(\"Am J Physiol Cell Physiol 279:C567-77 (2000)\", \"DOI:10.1152/ajpcell.2000.279.3.C567\")), list(REFERENCE = \"PMID:11032799\", AUTHORS = \"Fullner KJ, Mekalanos JJ.\", TITLE = \"In vivo covalent cross-linking of cellular actin by the Vibrio cholerae RTX toxin.\", JOURNAL = c(\"EMBO J 19:5315-23 (2000)\", \"DOI:10.1093/emboj/19.20.5315\")), list(REFERENCE = \"PMID:11292584\", AUTHORS = \"Lencer WI.\", TITLE = \"Microbes and microbial Toxins: paradigms for microbial-mucosal toxins. V. Cholera: invasion of the intestinal epithelial barrier by a stably folded protein toxin.\", JOURNAL = c(\"Am J Physiol Gastrointest Liver Physiol 280:G781-6 (2001)\", \"DOI:10.1152/ajpgi.2001.280.5.G781\")), list(REFERENCE = \"PMID:12102686\", AUTHORS = \"Moschioni M, Tombola F, de Bernard M, Coelho A, Zitzer A, Zoratti M, Montecucco C.\", TITLE = \"The Vibrio cholerae haemolysin anion channel is required for cell vacuolation and death.\", JOURNAL = c(\"Cell Microbiol 4:397-409 (2002)\", \"DOI:10.1046/j.1462-5822.2002.00199.x\")), list(REFERENCE = \"PMID:12403808\", AUTHORS = \"Tsai B, Rapoport TA.\", TITLE = \"Unfolded cholera toxin is transferred to the ER membrane and released from protein disulfide isomerase upon oxidation by Ero1.\", JOURNAL = c(\"J Cell Biol 159:207-16 (2002)\", \"DOI:10.1083/jcb.200207120\")), list(REFERENCE = \"PMID:13679513\", AUTHORS = \"Fujinaga Y, Wolf AA, Rodighiero C, Wheeler H, Tsai B, Allen L, Jobling MG, Rapoport T, Holmes RK, Lencer WI.\", TITLE = \"Gangliosides that associate with lipid rafts mediate transport of cholera and related toxins from the plasma membrane to endoplasmic reticulm.\", JOURNAL = c(\"Mol Biol Cell 14:4783-93 (2003)\", \"DOI:10.1091/mbc.E03-06-0354\")), list(REFERENCE = \"PMID:11953326\", AUTHORS = \"Fasano A.\", TITLE = \"Toxins and the gut: role in human disease.\", JOURNAL = c(\"Gut 50 Suppl 3:III9-14 (2002)\", \"DOI:10.1136/gut.50.suppl_3.iii9\")), list(REFERENCE = \"PMID:11207565\", AUTHORS = \"Uzzau S, Fasano A.\", TITLE = \"Cross-talk between enteric pathogens and the intestine.\", JOURNAL = c(\"Cell Microbiol 2:83-9 (2000)\", \"DOI:10.1046/j.1462-5822.2000.00041.x\")), list(REFERENCE = \"PMID:11349009\", AUTHORS = \"Sandkvist M.\", TITLE = \"Type II secretion and pathogenesis.\", JOURNAL = c(\"Infect Immun 69:3523-35 (2001)\", \"DOI:10.1128/IAI.69.6.3523-3535.2001\")), list(REFERENCE = \"PMID:12671039\", AUTHORS = \"Field M.\", TITLE = \"Intestinal ion transport and the pathophysiology of diarrhea.\", JOURNAL = c(\"J Clin Invest 111:931-43 (2003)\", \"DOI:10.1172/JCI18326\")), list(REFERENCE = \"PMID:14659695\", AUTHORS = \"Lencer WI, Tsai B.\", TITLE = \"The intracellular voyage of cholera toxin: going retro.\", JOURNAL = c(\"Trends Biochem Sci 28:639-45 (2003)\", \"DOI:10.1016/j.tibs.2003.10.002\")), list(REFERENCE = \"PMID:15204437\", AUTHORS = \"De Haan L, Hirst TR.\", TITLE = \"Cholera toxin: a paradigm for multi-functional engagement of cellular mechanisms (Review).\", JOURNAL = c(\"Mol Membr Biol 21:77-92 (2004)\", \"DOI:10.1080/09687680410001663267\")), list(REFERENCE = \"PMID:17156122\", AUTHORS = \"Chinnapen DJ, Chinnapen H, Saslowsky D, Lencer WI.\", TITLE = \"Rafting with cholera toxin: endocytosis and trafficking from plasma membrane to ER.\", JOURNAL = c(\"FEMS Microbiol Lett 266:129-37 (2007)\", \"DOI:10.1111/j.1574-6968.2006.00545.x\")), list(REFERENCE = \"PMID:17464284\", AUTHORS = \"Sheahan KL, Cordero CL, Satchell KJ.\", TITLE = \"Autoprocessing of the Vibrio cholerae RTX toxin by the cysteine protease domain.\", JOURNAL = c(\"EMBO J 26:2552-61 (2007)\", \"DOI:10.1038/sj.emboj.7601700\")), list(REFERENCE = \"PMID:11454785\", AUTHORS = \"Fasano A.\", TITLE = \"Intestinal zonulin: open sesame!\", JOURNAL = c(\"Gut 49:159-62 (2001)\", \"DOI:10.1136/gut.49.2.159\"))', 'map05110', 1, 10, 1, 0, 0, 0, 10, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (397, '05111', 'Biofilm formation - Vibrio cholerae', 'Surface colonization and subsequent biofilm formation and development provide numerous advantages to microorganisms. On the other hand, biofilm formation is an energetically costly process and therefore must be tightly regulated and plastic, enabling biofilm bacteria to be responsive to the various environmental cues. The quorum sensing (QS) pathway of Vibrio cholerae activates the expression of VpsR, VpsT and AphA (the main activators of biofilm formation) at low cell density and HapR (the main repressor) at high cell density. At low cell density, biofilm genes, including Vibrio polysaccharide (VPS) biosynthesis genes and the major extracellular matrix genes RbmA, RbmC, and Bap1, are expressed. The QS pathway also leads to induction of virulence factors such as toxin coregulated pilus (TCP) and cholera toxin (CTX), essential for colonization of the host and enterotoxicity, respectively. The VarS/VarA signaling system responds to an unknown environmental cue and represses biofilm production by process which involves the regulatory sRNAs CsrB, CsrC and CsrD and the RNA-binding protein CsrA, interfering with LuxO of the QS pathway. The second messenger cAMP acts as a repressor of biofilm formation. cAMP is generated by adenylate cyclase CyaA, and in complex with cAMP receptor protein (CRP), this nucleotide has been shown to upregulate HapR production through the positive regulation of the CAI-I synthase CqsA and the negative regulation of Fis. The second messenger c-di-GMP is a key signalling molecule controlling motility and biofilm matrix production. High cellular levels of c-di-GMP promote enhanced transcription of genes involved in biofilm formation, possibly by promoting VpsT-mediated transcriptional activation of vps genes.', 'Cellular Processes; Cellular community - prokaryotes', 'Biofilm formation - Vibrio cholerae', 'Vibrio cholerae pathogenicity signature, toxin coregulated pilus [PATH:map05111]', 'ko05111', 'list(REFERENCE = \"PMID:25895940\", AUTHORS = \"Teschler JK, Zamorano-Sanchez D, Utada AS, Warner CJ, Wong GC, Linington RG, Yildiz FH\", TITLE = \"Living in the matrix: assembly and control of Vibrio cholerae biofilms.\", JOURNAL = c(\"Nat Rev Microbiol 13:255-68 (2015)\", \"DOI:10.1038/nrmicro3433\")), list(REFERENCE = \"PMID:26700108\", AUTHORS = \"Dang H, Lovell CR\", TITLE = \"Microbial Surface Colonization and Biofilm Development in Marine Environments.\", JOURNAL = c(\"Microbiol Mol Biol Rev 80:91-138 (2016)\", \"DOI:10.1128/MMBR.00037-15\")), list(REFERENCE = \"PMID:19287449\", AUTHORS = \"Hengge R\", TITLE = \"Principles of c-di-GMP signalling in bacteria.\", JOURNAL = c(\"Nat Rev Microbiol 7:263-73 (2009)\", \"DOI:10.1038/nrmicro2109\")), list(REFERENCE = \"PMID:25666534\", AUTHORS = \"Liang ZX\", TITLE = \"The expanding roles of c-di-GMP in the biosynthesis of exopolysaccharides and secondary metabolites.\", JOURNAL = c(\"Nat Prod Rep 32:663-83 (2015)\", \"DOI:10.1039/c4np00086b\")), list(REFERENCE = \"PMID:9371445\", AUTHORS = \"Sandkvist M, Michel LO, Hough LP, Morales VM, Bagdasarian M, Koomey M, DiRita VJ, Bagdasarian M\", TITLE = \"General secretion pathway (eps) genes required for toxin secretion and outer membrane biogenesis in Vibrio cholerae.\", JOURNAL = c(\"J Bacteriol 179:6994-7003 (1997)\", \"DOI:10.1128/JB.179.22.6994-7003.1997\")), list(REFERENCE = \"PMID:16262799\", AUTHORS = \"Lenz DH, Miller MB, Zhu J, Kulkarni RV, Bassler BL\", TITLE = \"CsrA and three redundant small RNAs regulate quorum sensing in Vibrio cholerae.\", JOURNAL = c(\"Mol Microbiol 58:1186-202 (2005)\", \"DOI:10.1111/j.1365-2958.2005.04902.x\")), list(REFERENCE = \"PMID:17981973\", AUTHORS = \"Houot L, Watnick PI\", TITLE = \"A novel role for enzyme I of the Vibrio cholerae phosphoenolpyruvate phosphotransferase system in regulation of growth in a biofilm.\", JOURNAL = c(\"J Bacteriol 190:311-20 (2008)\", \"DOI:10.1128/JB.01410-07\")), list(REFERENCE = \"PMID:17921282\", AUTHORS = \"Liang W, Silva AJ, Benitez JA\", TITLE = \"The cyclic AMP receptor protein modulates colonial morphology in Vibrio cholerae.\", JOURNAL = c(\"Appl Environ Microbiol 73:7482-7 (2007)\", \"DOI:10.1128/AEM.01564-07\")), list(REFERENCE = \"PMID:17181781\", AUTHORS = \"Lenz DH, Bassler BL\", TITLE = \"The small nucleoid protein Fis is involved in Vibrio cholerae quorum sensing.\", JOURNAL = c(\"Mol Microbiol 63:859-71 (2007)\", \"DOI:10.1111/j.1365-2958.2006.05545.x\"))', 'map05111', 'Biofilm formation - Vibrio cholerae', 0, 50, 0, 0, 0, 0, 50, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (398, '05120', 'Epithelial cell signaling in Helicobacter pylori infection', 'c(\"Two major virulence factors of H. pylori are the vacuolating cytotoxin (VacA) and the cag type-IV secretion system (T4SS) and its translocated effector protein, cytotoxin-associated antigen A (CagA).\", \"VacA binds to lipid rafts and glycosylphosphatidylinositol-anchored proteins (GPI-APs) of the target cell membrane. After insertion into the plasma membrane, VacA channels are endocytosed and eventually reach late endosomal compartments, increasing their permeability to anions with enhancement of the electrogenic vacuolar ATPase (v-ATPase) proton pump. In the presence of weak bases, osmotically active acidotropic ions will accumulate in the endosomes. This leads to water influx and vesicle swelling, an essential step in vacuole formation. In addition, it is reported that the VacA cleavage product binds to the tyrosine phosphatase receptor zeta (Ptprz) on epithelial cells and the induced signaling leads to the phosphorylation of the G protein-coupled receptor kinase-interactor 1 (Git1) and induces ulcerogenesis in mice.\", \n\"The other virulence factor cag T4SS mediates the translocation of the effector protein CagA, which is subsequently phosphorylated by a Src kinase. Phosphorylated CagA interacts with the protein tyrosine phosphatase SHP-2, thus stimulating its phosphatase activity. Activated SHP-2 is able to induce MAPK signalling through Ras/Raf-dependent and -independent mechanisms. Deregulation of this pathway by CagA may lead to abnormal proliferation and movement of gastric epithelial cells.\")', 'Human Diseases; Infectious disease: bacterial', 'Epithelial cell signaling in Helicobacter pylori infection', 'Helicobacter pylori pathogenicity signature, cagA pathogenicity island [PATH:map05120]', 'Helicobacter pylori infection', 'ko05120', 'list(REFERENCE = \"PMID:14700549\", AUTHORS = \"Naumann M, Crabtree JE.\", TITLE = \"Helicobacter pylori-induced epithelial cell signalling in gastric carcinogenesis.\", JOURNAL = c(\"Trends Microbiol 12:29-36 (2004)\", \"DOI:10.1016/j.tim.2003.11.005\")), list(REFERENCE = \"PMID:15694859\", AUTHORS = \"Rieder G, Fischer W, Haas R.\", TITLE = \"Interaction of Helicobacter pylori with host cells: function of secreted and translocated molecules.\", JOURNAL = c(\"Curr Opin Microbiol 8:67-73 (2005)\", \"DOI:10.1016/j.mib.2004.12.004\")), list(REFERENCE = \"PMID:11389469\", AUTHORS = \"Montecucco C, Rappuoli R.\", TITLE = \"Living dangerously: how Helicobacter pylori survives in the human stomach.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 2:457-66 (2001)\", \"DOI:10.1038/35073084\")), list(REFERENCE = \"PMID:12197905\", AUTHORS = \"Gerhard M, Rad R, Prinz C, Naumann M.\", TITLE = \"Pathogenesis of Helicobacter pylori infection.\", JOURNAL = c(\"Helicobacter 7 Suppl 1:17-23 (2002)\", \"DOI:10.1046/j.1523-5378.7.s1.3.x\"))', 'map05120', 1, 8, 1, 0, 0, 0, 8, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (399, '05130', 'Pathogenic Escherichia coli infection', 'Enteropathogenic E. coli (EPEC) and enterohemorrhagic E. coli (EHEC) are closely related pathogenic strains of Escherichia coli. The hallmark of EPEC/EHEC infections [DS:H00278 H00277] is induction of attaching and effacing (A/E) lesions that damage intestinal epithelial cells. The capacity to form A/E lesions is encoded mainly by the locus of enterocyte effacement (LEE) pathogenicity island. Tir, Map, EspF, EspG are known LEE-encoded effector proteins secreted via the type III secretion system, which is also LEE-encoded, into the host cell. EPEC and EHEC Tir\'s link the extracellular bacterium to the cell cytoskeleton. Map and EspF are involved in mitochondrion membrane permeabilization. EspG interacts with tubulins and stimulates microtubule destabilization. LEE-encoded adhesin or intimin (Eae) is exported via the general secretory pathway to the periplasm, where it is inserted into the outer membrane. In addition to Tir, two potential host cell-carried intimin receptors, beta1 integrin (ITGB1) and nucleolin (NCL), have so far been identified. The distinguishing feature of EHEC is the elaboration of Shiga-like toxin (Stx). Stx cleaves ribosomal RNA, thereby disrupting protein synthesis and killing the intoxicated epithelial or endothelial cells.', 'Human Diseases; Infectious disease: bacterial', 'Pathogenic Escherichia coli infection', 'EHEC pathogenicity signature, Shiga toxin [PATH:map05130], EHEC/EPEC pathogenicity signature, T3SS and effectors [PATH:map05130]', 'Enterohemorrhagic Escherichia coli (EHEC) infection, Enteropathogenic Escherichia coli (EPEC) infection', 'ko05130', 'list(REFERENCE = \"PMID:29477730\", AUTHORS = \"Pinaud L, Sansonetti PJ, Phalipon A\", TITLE = \"Host Cell Targeting by Enteropathogenic Bacteria T3SS Effectors.\", JOURNAL = c(\"Trends Microbiol 26:266-283 (2018)\", \"DOI:10.1016/j.tim.2018.01.010\")), list(REFERENCE = \"PMID:25588886\", AUTHORS = \"Santos AS, Finlay BB\", TITLE = \"Bringing down the host: enteropathogenic and enterohaemorrhagic Escherichia coli effector-mediated subversion of host innate immune pathways.\", JOURNAL = c(\"Cell Microbiol 17:318-32 (2015)\", \"DOI:10.1111/cmi.12412\")), list(REFERENCE = \"PMID:26104206\", AUTHORS = \"Pearson JS, Hartland EL\", TITLE = \"The Inflammatory Response during Enterohemorrhagic Escherichia coli Infection.\", JOURNAL = c(\"Microbiol Spectr 2:EHEC-0012-2013 (2014)\", \"DOI:10.1128/microbiolspec.EHEC-0012-2013\")), list(REFERENCE = \"PMID:21488979\", AUTHORS = \"Wong AR, Pearson JS, Bright MD, Munera D, Robinson KS, Lee SF, Frankel G, Hartland EL\", TITLE = \"Enteropathogenic and enterohaemorrhagic Escherichia coli: even more subversive elements.\", JOURNAL = c(\"Mol Microbiol 80:1420-38 (2011)\", \"DOI:10.1111/j.1365-2958.2011.07661.x\")), list(REFERENCE = \"PMID:23927593\", AUTHORS = \"Lai Y, Rosenshine I, Leong JM, Frankel G\", TITLE = \"Intimate host attachment: enteropathogenic and enterohaemorrhagic Escherichia coli.\", JOURNAL = c(\"Cell Microbiol 15:1796-808 (2013)\", \"DOI:10.1111/cmi.12179\")), list(REFERENCE = \"PMID:27606286\", AUTHORS = \"Ugalde-Silva P, Gonzalez-Lugo O, Navarro-Garcia F\", TITLE = \"Tight Junction Disruption Induced by Type 3 Secretion System Effectors Injected by Enteropathogenic and Enterohemorrhagic Escherichia coli.\", JOURNAL = c(\"Front Cell Infect Microbiol 6:87 (2016)\", \"DOI:10.3389/fcimb.2016.00087\")), list(REFERENCE = \"PMID:27617233\", AUTHORS = \"Yen H, Karino M, Tobe T\", TITLE = \"Modulation of the Inflammasome Signaling Pathway by Enteropathogenic and Enterohemorrhagic Escherichia coli.\", JOURNAL = c(\"Front Cell Infect Microbiol 6:89 (2016)\", \"DOI:10.3389/fcimb.2016.00089\")), list(REFERENCE = \"PMID:27732847\", AUTHORS = \"Humphreys D, Singh V, Koronakis V\", TITLE = \"Inhibition of WAVE Regulatory Complex Activation by a Bacterial Virulence Effector Counteracts Pathogen Phagocytosis.\", JOURNAL = c(\"Cell Rep 17:697-707 (2016)\", \"DOI:10.1016/j.celrep.2016.09.039\")), list(REFERENCE = \"PMID:20300064\", AUTHORS = \"Dong N, Liu L, Shao F\", TITLE = \"A bacterial effector targets host DH-PH domain RhoGEFs and antagonizes macrophage phagocytosis.\", JOURNAL = c(\"EMBO J 29:1363-76 (2010)\", \"DOI:10.1038/emboj.2010.33\")), list(REFERENCE = \"PMID:15318166\", AUTHORS = \"Matsuzawa T, Kuwae A, Yoshida S, Sasakawa C, Abe A.\", TITLE = \"Enteropathogenic Escherichia coli activates the RhoA signaling pathway via the stimulation of GEF-H1.\", JOURNAL = c(\"EMBO J 23:3570-82 (2004)\", \"DOI:10.1038/sj.emboj.7600359\"))', 'map05130', 1, 13, 1, 0, 0, 0, 13, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (400, '05131', 'Shigellosis', 'Shigellosis, or bacillary dysentery, is an intestinal infection caused by Shigella, a genus of enterobacteria. Shigella are potential food-borne pathogens that are capable of colonizing the intestinal epithelium by exploiting epithelial-cell functions and circumventing the host innate immune response. During basolateral entry into the host-cell cytoplasm, Shigella deliver a subset of effectors into the host cells through the type III secretion system. The effectors induce membrane ruffling through the stimulation of the Rac1-WAVE-Arp2/3 pathway, enabling bacterial entry into the epithelial cells. During multiplication within the cells, Shigella secrete another subset of effectors. VirG induces actin polymerization at one pole of the bacteria, allowing the bacteria to spread intracellularly and to infect adjacent cells. OspF, OspG and IpaH(9.8) downregulate the production of proinflammatory cytokines such as IL-8, helping bacteria circumvent the innate immune response.', 'Human Diseases; Infectious disease: bacterial', 'Shigellosis', 'EHEC pathogenicity signature, Shiga toxin [PATH:map05131]', 'Shigellosis', 'ko05131', 'list(REFERENCE = \"PMID:30953429\", AUTHORS = \"Schnupf P, Sansonetti PJ\", TITLE = \"Shigella Pathogenesis: New Insights through Advanced Methodologies.\", JOURNAL = c(\"Microbiol Spectr 7 (2019)\", \"DOI:10.1128/microbiolspec.BAI-0023-2019\")), list(REFERENCE = \"PMID:28393050\", AUTHORS = \"Mattock E, Blocker AJ\", TITLE = \"How Do the Virulence Factors of Shigella Work Together to Cause Disease?\", JOURNAL = c(\"Front Cell Infect Microbiol 7:64 (2017)\", \"DOI:10.3389/fcimb.2017.00064\")), list(REFERENCE = \"PMID:25999954\", AUTHORS = \"Ashida H, Mimuro H, Sasakawa C\", TITLE = \"Shigella manipulates host immune responses by delivering effector proteins with specific roles.\", JOURNAL = c(\"Front Immunol 6:219 (2015)\", \"DOI:10.3389/fimmu.2015.00219\")), list(REFERENCE = \"PMID:20228623\", AUTHORS = \"Sasakawa C\", TITLE = \"A new paradigm of bacteria-gut interplay brought through the study of Shigella.\", JOURNAL = c(\"Proc Jpn Acad Ser B Phys Biol Sci 86:229-43 (2010)\", \"DOI:10.2183/pjab.86.229\")), list(REFERENCE = \"PMID:25264025\", AUTHORS = \"Ashida H, Kim M, Sasakawa C\", TITLE = \"Manipulation of the host cell death pathway by Shigella.\", JOURNAL = c(\"Cell Microbiol 16:1757-66 (2014)\", \"DOI:10.1111/cmi.12367\")), list(REFERENCE = \"PMID:29875765\", AUTHORS = \"Siqueira MDS, Ribeiro RM, Travassos LH\", TITLE = \"Autophagy and Its Interaction With Intracellular Bacterial Pathogens.\", JOURNAL = c(\"Front Immunol 9:935 (2018)\", \"DOI:10.3389/fimmu.2018.00935\")), list(REFERENCE = \"PMID:29477730\", AUTHORS = \"Pinaud L, Sansonetti PJ, Phalipon A\", TITLE = \"Host Cell Targeting by Enteropathogenic Bacteria T3SS Effectors.\", JOURNAL = c(\"Trends Microbiol 26:266-283 (2018)\", \"DOI:10.1016/j.tim.2018.01.010\"))', 'map05131', 1, 7, 1, 0, 0, 0, 7, 0);
