INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (406, '05142', 'Chagas disease (American trypanosomiasis)', 'Trypanosoma cruzi is an intracellular protozoan parasite that causes Chagas disease. The parasite life cycle involves hematophagous reduviid bugs as vectors. Once parasites enter the host body, they invade diverse host cells including cardiomyocytes. Establishment of infection depends on various parasite molecules such as cruzipain, oligopeptidase B, and trans-sialidase that activate Ca2+ signaling. Internalized parasites escape from the parasitophorous vacuole using secreted pore-forming TcTOX molecule and replicate in the cytosol. Multiplied parasites eventually lyse infected host cells and are released in the circulation. During these events, the parasites manipulate host innate immunity and elicit cardiomyocyte hypertrophy. T lymphocyte responses are also disturbed.', 'Human Diseases; Infectious disease: parasitic', 'Chagas disease (American trypanosomiasis)', 'Chagas disease', 'ko05142', 'list(REFERENCE = \"PMID:10234174\", AUTHORS = \"Bogdan C, Rollinghoff M\", TITLE = \"How do protozoan parasites survive inside macrophages?\", JOURNAL = c(\"Parasitol Today 15:22-8 (1999)\", \"DOI:10.1016/S0169-4758(98)01362-3\")), list(REFERENCE = \"PMID:17110955\", AUTHORS = \"Gazzinelli RT, Denkers EY\", TITLE = \"Protozoan encounters with Toll-like receptor signalling pathways: implications for host parasitism.\", JOURNAL = c(\"Nat Rev Immunol 6:895-906 (2006)\", \"DOI:10.1038/nri1978\")), list(REFERENCE = \"PMID:1717552\", AUTHORS = \"Norris KA, Bradt B, Cooper NR, So M\", TITLE = \"Characterization of a Trypanosoma cruzi C3 binding protein with functional and genetic similarities to the human c