INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (431, '05211', 'Renal cell carcinoma', 'Renal cell cancer (RCC) accounts for ~3% of human malignancies and its incidence appears to be rising. Although most cases of RCC seem to occur sporadically, an inherited predisposition to renal cancer accounts for 1-4% of cases. RCC is not a single disease, it has several morphological subtypes. Conventional RCC (clear cell RCC) accounts for ~80% of cases, followed by papillary RCC (10-15%), chromophobe RCC (5%), and collecting duct RCC (<1%). Genes potentially involved in sporadic neoplasms of each particular type are VHL, MET, BHD, and FH respectively. In the absence of VHL, hypoxia-inducible factor alpha (HIF-alpha) accumulates, leading to production of several growth factors, including vascular endothelial growth factor and platelet-derived growth factor. Activated MET mediates a number of biological effects including motility, invasion of extracellular matrix, cellular transformation, prevention of apoptosis and metastasis formation. Loss of functional FH leads to accumulation of fumarate in the cell, triggering inhibition of HPH and preventing targeted pVHL-mediated degradation of HIF-alpha. BHD mutations cause the Birt-Hogg-Dube syndrome and its associated chromophobe, hybrid oncocytic, and conventional (clear cell) RCC.', 'Human Diseases; Cancer: specific types', 'Renal cell carcinoma', 'Renal cell carcinoma, von Hippel-Lindau syndrome', 'ko05211', 'list(REFERENCE = \"PMID:16339096\", AUTHORS = \"Cohen HT, McGovern FJ.\", TITLE = \"Renal-cell carcinoma.\", JOURNAL = c(\"N Engl J Med 353:2477-90 (2005)\", \"DOI:10.1056/NEJMra043172\")), list(REFERENCE = \"PMID:15122209\", AUTHORS = \"Pavlovich CP, Schmidt LS.\", TITLE = \"Searching for the hereditary causes of renal-cell carcinoma.\", JOURNAL = c(\"Nat Rev Cancer 4:381-93 (2004)\", \"DOI:10.1038/nrc1364\")), list(REFERENCE = \"PMID:14634372\", AUTHORS = \"Linehan WM, Walther MM, Zbar B.\", TITLE = \"The genetic basis of cancer of the kidney.\", JOURNAL = c(\"J Urol 170:2163-72 (2003)\", \"DOI:10.1097/01.ju.0000096060.92397.ed\")), list(REFERENCE = \"PMID:15611513\", AUTHORS = \"Kim WY, Kaelin WG.\", TITLE = \"Role of VHL gene mutation in human cancer.\", JOURNAL = c(\"J Clin Oncol 22:4991-5004 (2004)\", \"DOI:10.1200/JCO.2004.05.061\")), list(REFERENCE = \"PMID:17211469\", AUTHORS = \"Sudarshan S, Linehan WM, Neckers L.\", TITLE = \"HIF and fumarate hydratase in renal cancer.\", JOURNAL = c(\"Br J Cancer 96:403-7 (2007)\", \"DOI:10.1038/sj.bjc.6603547\")), list(REFERENCE = \"PMID:17287871\", AUTHORS = \"Sudarshan S, Pinto PA, Neckers L, Linehan WM.\", TITLE = \"Mechanisms of disease: hereditary leiomyomatosis and renal cell cancer--a distinct form of hereditary kidney cancer.\", JOURNAL = c(\"Nat Clin Pract Urol 4:104-10 (2007)\", \"DOI:10.1038/ncpuro0711\")), list(REFERENCE = \"PMID:14685170\", AUTHORS = \"Birchmeier C, Birchmeier W, Gherardi E, Vande Woude GF.\", TITLE = \"Met, metastasis, motility and more.\", JOURNAL = c(\"Nat Rev Mol Cell Biol 4:915-25 (2003)\", \"DOI:10.1038/nrm1261\")), list(REFERENCE = \"PMID:18358634\", AUTHORS = \"Muller-Hocker J, Babaryka G, Schmid I, Jung A\", TITLE = \"Overexpression of cyclin D1, D3, and p21 in an infantile renal carcinoma with Xp11.2 TFE3-gene fusion.\", JOURNAL = c(\"Pathol Res Pract 204:589-97 (2008)\", \"DOI:10.1016/j.prp.2008.01.010\")), list(REFERENCE = \"PMID:12351585\", AUTHORS = \"Bodmer D, van den Hurk W, van Groningen JJ, Eleveld MJ, Martens GJ, Weterman MA, van Kessel AG.\", TITLE = \"Understanding familial and non-familial renal cell cancer.\", JOURNAL = c(\"Hum Mol Genet 11:2489-98 (2002)\", \"DOI:10.1093/hmg/11.20.2489\")), list(REFERENCE = \"PMID:250