", AUTHORS = \"Wei D, Le X, Zheng L, Wang L, Frey JA, Gao AC, Peng Z, Huang S, Xiong HQ, Abbruzzese JL, Xie K.\", TITLE = \"Stat3 activation regulates the expression of vascular endothelial growth factor and human pancreatic cancer angiogenesis and metastasis.\", JOURNAL = c(\"Oncogene 22:319-29 (2003)\", \"DOI:10.1038/sj.onc.1206122\")), list(REFERENCE = \"PMID:14580692\", AUTHORS = \"Toyonaga T, Nakano K, Nagano M, Zhao G, Yamaguchi K, Kuroki S, Eguchi T, Chijiiwa K, Tsuneyoshi M, Tanaka M.\", TITLE = \"Blockade of constitutively activated Janus kinase/signal transducer and activator of transcription-3 pathway inhibits growth of human pancreatic cancer.\", JOURNAL = c(\"Cancer Lett 201:107-16 (2003)\", \"DOI:10.1016/S0304-3835(03)00482-8\")), list(REFERENCE = \"PMID:12079267\", AUTHORS = \"Schmid RM.\", TITLE = \"Genetic basis of pancreatic cancer.\", JOURNAL = c(\"Best Pract Res Clin Gastroenterol 16:421-33 (2002)\", \"DOI:10.1053/bega.2002.0316\")), list(REFERENCE = \"PMID:15774796\", AUTHORS = \"Elliott RL, Blobe GC.\", TITLE = \"Role of transforming growth factor Beta in human cancer.\", JOURNAL = c(\"J Clin Oncol 23:2078-93 (2005)\", \"DOI:10.1200/JCO.2005.02.047\")), list(REFERENCE = \"PMID:15823750\", AUTHORS = \"Rudkin TM, Foulkes WD.\", TITLE = \"BRCA2: breaks, mistakes and failed separations.\", JOURNAL = c(\"Trends Mol Med 11:145-8 (2005)\", \"DOI:10.1016/j.molmed.2005.02.003\")), list(REFERENCE = \"PMID:11707511\", AUTHORS = \"Venkitaraman AR.\", TITLE = \"Functions of BRCA1 and BRCA2 in the biological response to DNA damage.\", JOURNAL = \"J Cell Sci 114:3591-8 (2001)\"), list(REFERENCE = \"PMID:23340292\", AUTHORS = \"Chang Z, Li Z, Wang X, Kang Y, Yuan Y, Niu J, Wang H, Chatterjee D, Fleming JB, Li M, Abbruzzese JL, Chiao PJ\", TITLE = \"Deciphering the mechanisms of tumorigenesis in human pancreatic ductal epithelial cells.\", JOURNAL = c(\"Clin Cancer Res 19:549-59 (2013)\", \"DOI:10.1158/1078-0432.CCR-12-0032\"))', 'map05212', 'Pancreatic cancer'