INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (433, '05213', 'Endometrial cancer', 'Endometrial cancer (EC) is the most common gynaecological malignancy and the fourth most common malignancy in women in the developed world after breast, colorectal and lung cancer. Two types of endometrial carcinoma are distinguished with respect to biology and clinical course. Type-I carcinoma is related to hyperestrogenism by association with endometrial hyperplasia, frequent expression of estrogen and progesterone receptors and younger age, whereas type-II carcinoma is unrelated to estrogen, associated with atrophic endometrium, frequent lack of estrogen and progesterone receptors and older age. The morphologic differences in these cancers are mirrored in their molecular genetic profile with type I showing defects in DNA-mismatch repair and mutations in PTEN, K-ras, and beta-catenin, and type II showing aneuploidy, p53 mutations, and her2/neu amplification.', 'Human Diseases; Cancer: specific types', 'Endometrial cancer', 'Endometrial cancer, Mismatch repair deficiency', 'ko05213', 'list(REFERENCE = \"PMID:15947972\", AUTHORS = \"Ryan AJ, Susil B, Jobling TW, Oehler MK.\", TITLE = \"Endometrial cancer.\", JOURNAL = c(\"Cell Tissue Res 322:53-61 (2005)\", \"DOI:10.1007/s00441-005-1109-5\")), list(REFERENCE = \"PMID:14747944\", AUTHORS = \"Lax SF.\", TITLE = \"Molecular genetic pathways in various types of endometrial carcinoma: from a phenotypical to a molecular-based classification.\", JOURNAL = c(\"Virchows Arch 444:213-23 (2004)\", \"DOI:10.1007/s00428-003-0947-3\")), list(REFERENCE = \"PMID:16508724\", AUTHORS = \"Shiozawa T, Konishi I.\", TITLE = \"Early endometrial carcinoma: clinicopathology, hormonal aspects, molecular genetics, diagnosis, and treatment.\", JOURNAL = c(\"Int J Clin Oncol 11:13-21 (2006)\", \"DOI:10.1007/s10147-005-0546-1\")), list(REFERENCE = \"PMID:17028294\", AUTHORS = \"Hecht JL, Mutter GL.\", TITLE = \"Molecular and pathologic aspects of endometrial carcinogenesis.\", JOURNAL = c(\"J Clin Oncol 24:4783-91 (2006)\", \"DOI:10.1200/JCO.2006.06.7173\")), list(REFERENCE = \"PMID:11431710\", AUTHORS = \"Matias-Guiu X, Catasus L, Bussaglia E, Lagarda H, Garcia A, Pons C, Munoz J, Arguelles R, Machin P, Prat J.\", TITLE = \"Molecular pathology of endometrial hyperplasia and carcinoma.\", JOURNAL = c(\"Hum Pathol 32:569-77 (2001)\", \"DOI:10.1053/hupa.2001.25929\")), list(REFERENCE = \"PMID:15284264\", AUTHORS = \"Slomovitz BM, Broaddus RR, Burke TW, Sneige N, Soliman PT, Wu W, Sun CC, Munsell MF, Gershenson DM, Lu KH.\", TITLE = \"Her-2/neu overexpression and amplification in uterine papillary serous carcinoma.\", JOURNAL = c(\"J Clin Oncol 22:3126-32 (2004)\", \"DOI:10.1200/JCO.2004.11.154\")), list(REFERENCE = \"PMID:7585656\", AUTHORS = \"Saffari B, Jones LA, el-Naggar A, Felix JC, George J, Press MF.\", TITLE = \"Amplification and overexpression of HER-2/neu (c-erbB2) in endometrial cancers: correlation with overall survival.\", JOURNAL = \"Cancer Res 55:5693-8 (1995)\"), list(REFERENCE = \"PMID:16248975\", AUTHORS = \"Kim D, Chung J.\", TITLE = \"Akt: versatile mediator of cell survival and beyond.\", JOURNAL = \"J Biochem Mol Biol 35:106-15 (2002)\")', 'map05213', 'Endometrial cancer', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (434, '05214', 'Glioma', 'Gliomas are the most common of the primary brain tumors and account for more than 40% of all central nervous system neoplasms. Gliomas include tumours that are composed predominantly of astrocytes (astrocytomas), oligodendrocytes (oligodendrogliomas), mixtures of various glial cells (for example,oligoastrocytomas) and ependymal cells (ependymomas). The most malignant form of infiltrating astrocytoma - glioblastoma multiforme (GBM) - is one of the most aggressive human cancers. GBM may develop de novo (primary glioblastoma) or by progression from low-grade or anaplastic astrocytoma (secondary glioblastoma). Primary glioblastomas develop in older patients and typically show genetic alterations (EGFR amplification, p16/INK4a deletion, and PTEN mutations) at frequencies of 24-34%. Secondary glioblastomas develop in younger patients and frequently show overexpression of PDGF and CDK4 as well as p53 mutations (65%) and loss of Rb playing major roles in such transformations. Loss of PTEN has been implicated in both pathways, although it is much more common in the pathogenesis of primary GBM.', 'Human Diseases; Cancer: specific types', 'Glioma', 'Glioma', 'ko05214', 'list(REFERENCE = \"PMID:15639402\", AUTHORS = \"Soni D, King JA, Kaye AH, Hovens CM.\", TITLE = \"Genetics of glioblastoma multiforme: mitogenic signaling and cell cycle pathways converge.\", JOURNAL = c(\"J Clin Neurosci 12:1-5 (2005)\", \"DOI:10.1016/j.jocn.2004.04.001\")), list(REFERENCE = \"PMID:12154354\", AUTHORS = \"Zhu Y, Parada LF.\", TITLE = \"The molecular and genetic basis of neurological tumours.\", JOURNAL = c(\"Nat Rev Cancer 2:616-26 (2002)\", \"DOI:10.