INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (436, '05216', 'Thyroid cancer', 'Thyroid cancer is the most common endocrine malignancy and accounts for the majority of endocrine cancer- related deaths each year. More than 95% of thyroid carcinomas are derived from follicular cells. Their behavior varies from the indolent growing, well-differentiated papillary and follicular carcinomas (PTC and FTC, respectively) to the extremely aggressive undifferentiated carcinoma (UC). Somatic rearrangements of RET and TRK are almost exclusively found in PTC and may be found in early stages. The most distinctive molecular features of FTC are the prominence of aneuploidy and the high prevalence of RAS mutations and PAX8-PPAR{gamma} rearrangements. p53 seems to play a crucial role in the dedifferentiation process of thyroid carcinoma.', 'Human Diseases; Cancer: specific types', 'Thyroid cancer', 'Thyroid cancer', 'ko05216', 'list(REFERENCE = \"PMID:16557281\", AUTHORS = \"Kondo T, Ezzat S, Asa SL.\", TITLE = \"Pathogenetic mechanisms in thyroid follicular-cell neoplasia.\", JOURNAL = c(\"Nat Rev Cancer 6:292-306 (2006)\", \"DOI:10.1038/nrc1836\")), list(REFERENCE = \"PMID:16551846\", AUTHORS = \"Chiloeches A, Marais R.\", TITLE = \"Is BRAF the Achilles\' Heel of thyroid cancer?\", JOURNAL = c(\"Clin Cancer Res 12:1661-4 (2006)\", \"DOI:10.1158/1078-0432.CCR-06-0051\")), list(REFERENCE = \"PMID:16946003\", AUTHORS = \"Reddi HV, McIver B, Grebe SK, Eberhardt NL\", TITLE = \"The paired box-8/peroxisome proliferator-activated receptor-gamma oncogene in thyroid tumorigenesis.\", JOURNAL = c(\"Endocrinology 148:932-5 (2007)\", \"DOI:10.1210/en.2006-0926\")), list(REFERENCE = \"PMID:17062879\", AUTHORS = \"Santoro M,