INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (437, '05217', 'Basal cell carcinoma', 'Cancer of the skin is the most common cancer in Caucasians and basal cell carcinomas (BCC) account for 90% of all skin cancers. The vast majority of BCC cases are sporadic, though there is a rare familial syndrome basal cell nevus syndrome (BCNS, or Gorlin syndrome) that predisposes to development of BCC. In addition, there is strong epidemiological and genetic evidence that demonstrates UV exposure as a risk factor of prime importance. The development of basal cell carcinoma is associated with constitutive activation of sonic hedgehog signaling. The mutations in SMOH, PTCH1, and SHH in BCCs result in continuous activation of target genes. At a cellular level, sonic hedgehog signaling promotes cell proliferation. Mutations in TP53 are also found with high frequency (>50%) in sporadic BCC.', 'Human Diseases; Cancer: specific types', 'Basal cell carcinoma', 'Basal cell carcinoma, Basal cell nevus syndrome', 'ko05217', 'list(REFERENCE = \"PMID:15948971\", AUTHORS = \"Tilli CM, Van Steensel MA, Krekels GA, Neumann HA, Ramaekers FC.\", TITLE = \"Molecular aetiology and pathogenesis of basal cell carcinoma.\", JOURNAL = c(\"Br J Dermatol 152:1108-24 (2005)\", \"DOI:10.1111/j.1365-2133.2005.06587.x\")), list(REFERENCE = \"PMID:12588368\", AUTHORS = \"Saldanha G, Fletcher A, Slater DN.\", TITLE = \"Basal cell carcinoma: a dermatopathological and molecular biological update.\", JOURNAL = c(\"Br J Dermatol 148:195-202 (2003)\", \"DOI:10.1046/j.1365-2133.2003.05151.x\")), list(REFERENCE = \"PMID:15978322\", AUTHORS = \"Daya-Grosjean L, Couve-Privat S.\", TITLE = \"Sonic hedgehog signaling in basal cell carcinomas.\", JOU