INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (441, '05221', 'Acute myeloid leukemia', 'Acute myeloid leukemia (AML) is a disease that is characterized by uncontrolled proliferation of clonal neoplastic cells and accumulation in the bone marrow of blasts with an impaired differentiation program. AML accounts for approximately 80% of all adult leukemias and remains the most common cause of leukemia death. Two major types of genetic events have been described that are crucial for leukemic transformation. A proposed necessary first event is disordered cell growth and upregulation of cell survival genes. The most common of these activating events were observed in the RTK Flt3, in N-Ras and K-Ras, in Kit, and sporadically in other RTKs. Alterations in myeloid transcription factors governing hematopoietic differentiation provide second necessary event for leukemogenesis. Transcription factor fusion proteins such as AML-ETO, PML-RARalpha or PLZF-RARalpha block myeloid cell differentiation by repressing target genes. In other cases, the transcription factors themselves are mutated.', 'Human Diseases; Cancer: specific types', 'Acute myeloid leukemia', 'Acute myeloid leukemia', 'ko05221', 'list(REFERENCE = \"PMID:16236521\", AUTHORS = \"Steffen B, Muller-Tidow C, Schwable J, Berdel WE, Serve H.\", TITLE = \"The molecular pathogenesis of acute myeloid leukemia.\", JOURNAL = c(\"Crit Rev Oncol Hematol 56:195-221 (2005)\", \"DOI:10.1016/j.critrevonc.2004.10.012\")), list(REFERENCE = \"PMID:12563308\", AUTHORS = \"Tenen DG.\", TITLE = \"Disruption of differentiation in human cancer: AML shows the way.\", JOURNAL = c(\"Nat Rev Cancer 3:89-101 (2003)\", \"DOI:10.1038/nrc989\")), list(REFERENCE = \"PMID:1614683