INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (451, '05320', 'Autoimmune thyroid disease', 'The classification of autoimmune throid disease (AITD) includes Hashimoto\'s thyroiditis (HT) or chronic autoimmune thyroiditis and its variants, Graves\' disease (GD) and autoimmune atrophic thyroiditis or primary myxedema. HT is characterized by the presence of goitre, thyroid autoantibodies against thyroid peroxidase (TPO) and thyroglobulin (Tg) in serum and varying degrees of thyroid dysfunction. During HT, self-reactive CD4+ T lymphocytes (Th) recruit B cells and CD8+ T cells (CTL) into the thyroid. Disease progression leads to the death of thyroid cells and hypothyroidism. Both autoantibodies and thyroid-specific cytotoxic T lymphocytes (CTLs) have been proposed to be responsible for autoimmune thyrocyte depletion. In GD, the TSH-R is the most important autoantigen. Antibodies directed against it mimic the effects of the hormone on thyroid cells, TSH, stimulating autonomous production of thyroxine and triiodothyronine and causing hyperthyroidism. The presence of TSH-R-blocking antibodies that bind the TSH receptor in a similar fashion to the antibodies in patients with Grave\'s disease but that block rather than activate the receptor explains some cases of atrophic hypothyroidism.', 'Human Diseases; Immune disease', 'Autoimmune thyroid disease', 'Hashimoto thyroiditis, Graves disease', 'ko05320', 'list(REFERENCE = \"PMID:15049952\", AUTHORS = \"Fountoulakis S, Tsatsoulis A.\", TITLE = \"On the pathogenesis of autoimmune thyroid disease: a unifying hypothesis.\", JOURNAL = c(\"Clin Endocrinol (Oxf) 60:397-409 (2004)\", \"DOI:10.1046/j.1365-2265.2004.01978.x\")), list(REFERENCE = \"PMID:15762980\", AUTHORS = \"Chistiakov DA.\", TITLE = \"Immunogenetics of Hashimoto\'s thyroiditis.\", JOURNAL = c(\"J Autoimmune Dis 2:1 (2005)\", \"DOI:10.1186/1740-2557-2-1\")), list(REFERENCE = \"PMID:9534029\", AUTHORS = \"McIver B, Morris JC.\", TITLE = \"The pathogenesis of Graves\' disease.\", JOURNAL = c(\"Endocrinol Metab Clin North Am 27:73-89 (1998)\", \"DOI:10.1016/S0889-8529(05)70299-1\")), list(REFERENCE = \"PMID:11913070\", AUTHORS = \"Stassi G, De Maria R.\", TITLE = \"Autoimmune thyroid disease: new models of cell death in autoimmunity.\", JOURNAL = c(\"Nat Rev Immunol 2:195-204 (2002)\", \"DOI:10.1038/nri750\")), list(REFERENCE = \"PMID:12192541\", AUTHORS = \"Collins J, Gough S.\", TITLE = \"Autoimmunity in thyroid disease.\", JOURNAL = c(\"Eur J Nucl Med Mol Imaging 29 Suppl 2:S417-24 (2002)\", \"DOI:10.1007/s00259-002-0848-8\")), list(REFERENCE = \"PMID:11396701\", AUTHORS = \"Ruwhof C, Drexhage HA.\", TITLE = \"Iodine and thyroid autoimmune disease in animal models.\", JOURNAL = c(\"Thyroid 11:427-36 (2001)\", \"DOI:10.1089/105072501300176381\")), list(REFERENCE = \"PMID:17192582\", AUTHORS = \"Tsatsoulis A.\", TITLE = \"The role of stress in the clinical expression of thyroid autoimmunity.\", JOURNAL = c(\"Ann N Y Acad Sci 1088:382-95 (2006)\", \"DOI:10.1196/annals.1366.015\")), list(REFERENCE = \"PMID:15132715\", AUTHORS = \"Prummel MF, Strieder T, Wiersinga WM.\", TITLE = \"The environment and autoimmune thyroid diseases.\", JOURNAL = c(\"Eur J Endocrinol 150:605-18 (2004)\", \"DOI:10.1530/eje.0.1500605\")), list(REFERENCE = \"PMID:11577986\", AUTHORS = \"Kopp P.\", TITLE = \"The TSH receptor and its role in thyroid disease.\", JOURNAL = c(\"Cell Mol Life Sci 58:1301-22 (2001)\", \"DOI:10.1007/PL00000941\"))', 'map05320', 'Autoimmune thyroid disease', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (452, '05321', 'Inflammatory bowel disease (IBD)', 'Inflammatory bowel disease (IBD), which includes Crohn disease (CD) and ulcerative colitis (UC), is characterized by chronic inflammation of the gastrointestinal tract due to environmental and genetic factors, infectious microbes, and the dysregulated immune system. Although many environmental factors (for example, geographic locations, smoking, etc.) affect the development of IBD, the most crucial might be the luminal (external) environment of the epithelial cells. There are pathogens that are found in increasing frequency in IBD. The microbial components such as flagellin, peptidoglycan, and lipopolysaccharide are recognized by receptors such as toll-like receptors (TLRs) and nucleotide-binding oligomerization domain (NOD) proteins, and also by antigen-presenting cells (APCs) in genetically susceptible host. The TLR recognition triggers the activation of NF-kappaB, leading to an inflammatory response. APC-expressed gene NOD2 has been associated with Crohn disease. In case of mutations of NOD2, negative regulation of IL-12 production is reduced with the stimulation of muramyl dipeptide (MDP), leading to CD. In addition, the APC mediates the differentiation of naive T cells into effector T cells (Th1, Th17, Th2) and natural killer T (NKT) cells. Th1 and Th17 cells produce high levels of IFN-gamma and IL-17, -22, respectively, both of which promote CD. In contrast, Th2 cells produce IL-4, -5, -10, which together with IL-13 from NKT induce UC.', 'Human Diseases; Immune disease', 'Inflammatory bowel disease (IBD)', 'Crohn disease, Inflammatory bowel disease (IBD), Ulcerative colitis, IPEX syndrome', '