INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (455, '05330', 'Allograft rejection', 'Allograft rejection is the consequence of the recipient\'s alloimmune response to nonself antigens expressed by donor tissues. After transplantation of organ allografts, there are two pathways of antigen presentation. In the direct pathway, recipient T cells react to intact allogeneic MHC molecules expressed on the surface of donor cells. This pathway would activate host CD4 or CD8 T cells. In contrast, donor MHC molecules (and all other proteins) shed from the graft can be taken up by host APCs and presented to recipient T cells in the context of self-MHC molecules - the indirect pathway. Such presentation activates predominantly CD4 T cells. A direct cytotoxic T-cell attack on graft cells can be made only by T cells that recognize the graft MHC molecules directly. Nontheless, T cells with indirect allospecificity can contribute to graft rejection by activating macrophages, which cause tissue injury and fibrosis, and are also likely to be important in the development of an alloantibody response to graft.', 'Human Diseases; Immune disease', 'Allograft rejection', 'Allograft rejection', 'ko05330', 'list(REFERENCE = \"PMID:10746853\", AUTHORS = \"Arakelov A, Lakkis FG.\", TITLE = \"The alloimmune response and effector mechanisms of allograft rejection.\", JOURNAL = \"Semin Nephrol 20:95-102 (2000)\"), list(REFERENCE = \"PMID:12023610\", AUTHORS = \"Le Moine A, Goldman M, Abramowicz D.\", TITLE = \"Multiple pathways to allograft rejection.\", JOURNAL = \"Transplantation 73:1373-81 (2002)\"), list(REFERENCE = \"PMID:17445565\", AUTHORS = \"Trivedi HL.\", TITLE = \"Immunobiology of rejection and adaptation.\", JOURNAL = c(\"Transplant Proc 39:647-52 (2007)\", \"DOI:10.1016/j.transproceed.2007.01.047\")), list(REFERENCE = \"PMID:14710779\", AUTHORS = \"Pietra BA.\", TITLE = \"Transplantation immunology 2003: simplified approach.\", JOURNAL = c(\"Pediatr Clin North Am 50:1233-59 (2003)\", \"DOI:10.1016/S0031-3955(03)00119-6\")), list(REFERENCE = \"PMID:8835634\", AUTHORS = \"Oka T, Yoshimura N.\", TITLE = \"Immunosuppression in organ transplantation.\", JOURNAL = c(\"Jpn J Pharmacol 71:89-100 (1996)\", \"DOI:10.1254/jjp.71.89\")), list(REFERENCE = \"PMID:10571983\", AUTHORS = \"Sayegh MH.\", TITLE = \"Why do we reject a graft? Role of indirect allorecognition in graft rejection.\", JOURNAL = c(\"Kidney Int 56:1967-79 (1999)\", \"DOI:10.1046/j.1523-1755.1999.00751.x\")), list(REFERENCE = \"PMID:10331992\", AUTHORS = \"Benichou G.\", TITLE = \"Direct and indirect antigen recognition: the pathways to allograft immune rejection.\", JOURNAL = \"Front Biosci 4:D476-80 (1999)\"), list(REFERENCE = \"PMID:10667806\", AUTHORS = \"Turvey SE, Wood KJ.\", TITLE = \"Immunobiology of solid organ transplantation.\", JOURNAL = \"Int Surg 84:279-90 (1999)\"), list(REFERENCE = \"\", AUTHORS = \"Janeway CA, Travers P, Walport M, Shlomchik MJ.\", TITLE = \"Immunobiology 6th edition\", JOURNAL = \"Garland Science Publishing (2005)\"), list(REFERENCE = \"PMID:12216939\", AUTHORS = \"Game DS, Lechler RI.\", TITLE = \"Pathways of allorecognition: implications for transplantation tolerance.\", JOURNAL = c(\"Transpl Immunol 10:101-8 (2002)\", \"DOI:10.1016/S0966-3274(02)00055-2\")), list(REFERENCE = \"PMID:12563298\", AUTHORS = \"Lechler RI, Garden OA, Turka LA.\", TITLE = \"The complementary roles of deletion and regulation in transplantation tolerance.\", JOURNAL = c(\"Nat Rev Immunol 3:147-58 (2003)\", \"DOI:10.1038/nri1002\"))', 'map05330', 'Allograft rejection', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (456, '05332', 'Graft-versus-host disease', 'Graft-versus-host disease (GVHD) is a lethal complication of allogeneic hematopoietic stem cell transplantation (HSCT) where immunocompetent donor T cells attack the genetically disparate host cells. GVHD pathophysiology can be summerized in a three-step process. Step 1 involves the development of an inflammatory milieu resulting from damage in the host tissues induced by the preparative chemotherapy or radiotherapy regimen. Damaged tissues secrete inflammatory cytokines, including interleukin 1 (IL-1), and tumor necrosis factor (TNF-alpha ). During step 2, antigen-presenting cells (APCs) trigger the activation of donor-derived T cells, which induce further T-cell expansion, induce cytotoxic T lymphocytes (CTL) and natural killer (NK) cells responses and prime additional mononuclear phagocytes to produce TNF-alpha and IL-1. Also, nitric oxide (NO) is produced by activated macrophages, and it may contribute to the tissue damage seen during step 3. During step 3, the effector phase, activated CTL and NK cells mediate cytotoxicity against target host cells through Fas-Fas ligand interactions and perforin-granzyme B.', 'Human Diseases; Immune disease', 'Graft-versus-host disease', 'Graft-versus-host disease', 'ko05332', 'list(REFERENCE = \"PMID:16412784\", AUTHORS = \"Ferrara JL, Reddy P.\", TITLE = \"Pathophysiology of graft-versus-host disease.\", JOURNAL = c(\"Semin Hematol 43:3-10 (2006)\", \"DOI:10.1053/j.seminhematol.2005.09.001\")), list(REFERENCE = \"PMID:14735553\", AUTHORS = \"Reddy P.\", TITLE = \"Pathophysiology of acute graft-versus-host disease.\", JOURNAL = c(\"Hematol Oncol 21:149-61 (2003)\", \"DOI:10.1002/hon.716\")), list(REFERENCE = \"PMID:17900507\", AUTHORS = \"Sun Y, Tawara I, Toubai T, Reddy P.\", TITLE = \"Pathophysiology of acute graft-versus-host disease: recent advances.\", JOURNAL = c(\"Transl Res 150:197-214 (2007)\", \"DOI:10.1016/j.trsl.2007.06.003\")), list(REFERENCE = \"PMID:15882431\", AUTHORS = \"Jaksch M, Mattsson J.\", TITLE = \"The pathophysiology of acute graft-versus-host disease.\", JOURNAL = c(\"Scand J Immunol 61:398-409 (2005)\", \"DOI:10.1111/j.1365-3083.2005.01595.x\")), list(REFERENCE = \"PMID:12359826\", AUTHORS = \"Vogelsang GB, Lee L, Bensen-Kennedy DM.\", TITLE = \"Pathogenesis and treatment of graft-versus-host disease after bone marrow transplant.\", JOURNAL = c(\"Annu Rev Med 54:29-52 (2003)\", \"DOI:10.1146/annurev.med.54.101601.152339\")), list(REFERENCE = \"PMID:14556773\", AUTHORS = \"Reddy P, Ferrara JL.\", TITLE = \"Immunobiology of acute graft-versus-host disease.\", JOURNAL = c(\"Blood Rev 17:187-94 (2003)\", \"DOI:10.1016/S0268-960X(03)00009-2\")), list(REFERENCE = \"PMID:16147539\", AUTHORS = \"Blazar BR, Murphy WJ.\", TITLE = \"Bone marrow transplantation and approaches to avoid graft-versus-host disease (GVHD).\", JOURNAL = c(\"Philos Trans R Soc Lond B Biol Sci 360:1747-67 (2005)\", \"DOI:10.1098/rstb.2005.1701\")), list(REFERENCE = \"PMID:15372473\", AUTHORS = \"Couriel D, Caldera H, Champlin R, Komanduri K.\", TITLE = \"Acute graft-versus-host disease: pathophysiology, clinical manifestations, and management.\", JOURNAL = c(\"Cancer 101:1936-46 (2004)\", \"DOI:10.1002/cncr.20613\")), list(REFERENCE = \"PMID:17784964\", AUTHORS = \"Jacobsohn DA, Vogelsang GB.\", TITLE = \"Acute graft versus host disease.\", JOURNAL = c(\"Orphanet J Rare Dis 2:35 (2007)\", \"DOI:10.1186/1750-1172-2-35\"))', 'map05332', 'Graft-versus-host disease', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (457, '05340', 'Primary immunodeficiency', 'Primary immunodeficiencies (PIs) are a heterogeneous group of disorders, which affect cellular and humoral immunity or non-specific host defense mechanisms mediated by complement proteins, and cells such as phagocytes and natural killer (NK) cells. These disorders of the immune system cause increased susceptibility to infection, autoimmune disease, and malignancy. Most of PIs are due to genetic defects that affect cell maturation or function at different levels during hematopoiesis. Disruption of the cellular immunity is observed in patients with defects in T cells or both T and B cells. These cellular immunodeficiencies comprise 20% of all PIs. Disorders of humoral immunity affect B-cell differentiation and antibody production. They account for 70% of all PIs.', 'Human Diseases; Immune disease', 'Primary immunodeficiency', 'Agammaglobulinemias, Hyper IgM syndromes, autosomal recessive type, Common variable immunodeficiency, T-B+Severe combined immunodeficiency, T-B-Severe combined immunodeficiency, Combined immunodeficiency, Ectodermal dysplasia associated immunodeficiency, Other well-defined immunodeficiency syndromes, Bare lymphocyte syndrome type1, Bare lymphocyte syndrome type2, Autoimmune polyendocrinopathy syndrome type 1, Adenosine deaminase deficiency', 'ko05340', 'list(REFERENCE = \"PMID:16261175\", AUTHORS = \"Cunningham-Rundles C, Ponda PP.\", TITLE = \"Molecular defects in T- and B-cell primary immunodeficiency diseases.\", JOURNAL = c(\"Nat Rev Immunol 5:880-92 (2005)\", \"DOI:10.1038/nri1713\")), list(REFERENCE = \"PMID:11941303\", AUTHORS = \"Ballow M.\", TITLE = \"Primary immunodeficiency disorders: antibody deficiency.\", JOURNAL = c(\"J Allergy Clin Immunol 109:581-91 (2002)\", \"DOI:10.1067/mai.2002.122466\")), list(REFERENCE = \"PMID:17960151\", AUTHORS = \"Marodi L, Notarangelo LD.\", TITLE = \"Immunological and genetic bases of new primary immunodeficiencies.\", JOURNAL = c(\"Nat Rev Immunol 7:851-61 (2007)\", \"DOI:10.1038/nri2195\")), list(REFERENCE = \"PMID:17952897\", AUTHORS = \"Geha RS, Notarangelo LD, Casanova JL, Chapel H, Conley ME, Fischer A, Hammarstrom L, Nonoyama S, Ochs HD, Puck JM, Roifman C, Seger R, Wedgwood J.\", TITLE = \"Primary immunodeficiency diseases: an update from the International Union of Immunological Societies Primary Immunodeficiency Diseases Classification Committee.\", JOURNAL = c(\"J Allergy Clin Immunol 120:776-94 (2007)\", \"DOI:10.1016/j.jaci.2007.08.053\")), list(REFERENCE = \"PMID:18093537\", AUTHORS = \"Fischer A.\", TITLE = \"Human primary immunodeficiency diseases.\", JOURNAL = c(\"Immunity 27:835-45 (2007)\", \"DOI:10.1016/j.immuni.2007.11.012\")), list(REFERENCE = \"PMID:14699405\", AUTHORS = \"Fischer A.\", TITLE = \"Human primary immunodeficiency diseases: a perspective.\", JOURNAL = c(\"Nat Immunol 5:23-30 (2004)\", \"DOI:10.1038/ni1023\")), list(REFERENCE = \"PMID:11058677\", AUTHORS = \"Buckley RH.\", TITLE = \"Primary immunodeficiency diseases due to defects in lymphocytes.\", JOURNAL = c(\"N Engl J Med 343:1313-24 (2000)\", \"DOI:10.1056/NEJM200011023431806\")), list(REFERENCE = \"PMID:15661024\", AUTHORS = \"Fischer A, Le Deist F, Hacein-Bey-Abina S, Andre-Schmutz I, Basile Gde S, de Villartay JP, Cavazzana-Calvo M.\", TITLE = \"Severe combined immunodeficiency. A model disease for molecular immunology and therapy.\", JOURNAL = c(\"Immunol Rev 203:98-109 (2005)\", \"DOI:10.1111/j.0105-2896.2005.00223.x\")), list(REFERENCE = \"PMID:14647478\", AUTHORS = \"de Villartay JP, Fischer A, Durandy A.\", TITLE = \"The mechanisms of immune diversification and their disorders.\", JOURNAL = c(\"Nat Rev Immunol 3:962-72 (2003)\", \"DOI:10.1038/nri1247\"))', 'map05340', 'Primary immunodeficiency', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (458, '05410', 'Hypertrophic cardiomyopathy (HCM)', 'Hypertrophic cardiomyopathy (HCM) is a primary myocardial disorder with an autosomal dominant pattern of inheritance that is characterized by hypertrophy of the left ventricles with histological features of myocyte hypertrophy, myfibrillar disarray, and interstitial fibrosis. HCM is one of the most common inherited cardiac disorders, with a prevalence in young adults of 1 in 500. Hundreds of mutations in the genes that encode protein constituents of the sarcomere have been identified in HCM. These mutations increase the Ca2+ sensitivity of cardiac myofilaments. Increased myofilament Ca2+ sensitivity is expected to increase the ATP utilization by actomyosin at submaximal Ca2+ concentrations, which might cause an imbalance in energy supply and demand in the heart under severe stress. The inefficient use of ATP suggests that an inability to maintain normal ATP levels could be the central abnormality. This theory might be supported by the discovery of the role of a mutant PRKAG2 gene in HCM, which in active form acts as a central sensing mechanism protecting cells from depletion of ATP supplies. The increase in the myfilament Ca2+ sensitivity well account for the diastolic dysfunction of model animals as well as human patients of HCM. It has been widely proposed that left ventricular hypertrophy is not a primary manifestation but develops as compensatory response to sarcomere dysfunction.', 'Human Diseases; Cardiovascular disease', 'Hypertrophic cardiomyopathy (HCM)', 'Hypertrophic cardiomyopathy, Brugada syndrome, Wolff-Parkinson-White (WPW) syndrome', 'ko05410', 'list(REFERENCE = \"PMID:12270949\", AUTHORS = \"Fatkin D, Graham RM\", TITLE = \"Molecular mechanisms of inherited cardiomyopathies.\", JOURNAL = c(\"Physiol Rev 82:945-80 (2002)\", \"DOI:10.1152/physrev.00012.2002\")), list(REFERENCE = \"PMID:12844200\", AUTHORS = \"Moolman-Smook JC, Mayosi BM, Brink PA, Corfield VA\", TITLE = \"Molecular genetics of cardiomyopathy: changing times, shifting paradigms.\", JOURNAL = \"Cardiovasc J S Afr 14:145-55 (2003)\"), list(REFERENCE = \"PMID:14711353\", AUTHORS = \"Taylor MR, Carniel E, Mestroni L\", TITLE = \"Familial hypertrophic cardiomyopathy: clinical features, molecular genetics and molecular genetic testing.\", JOURNAL = c(\"Expert Rev Mol Diagn 4:99-113 (2004)\", \"DOI:10.1586/14737159.4.1.99\")), list(REFERENCE = \"PMID:10615904\", AUTHORS = \"Marian AJ\", TITLE = \"Pathogenesis of diverse clinical and pathological phenotypes in hypertrophic cardiomyopathy.\", JOURNAL = c(\"Lancet 355:58-60 (2000)\", \"DOI:10.1016/S0140-6736(99)06187-5\")), list(REFERENCE = \"PMID:11371514\", AUTHORS = \"Blair E, Redwood C, Ashrafian H, Oliveira M, Broxholme J, Kerr B, Salmon A, Ostman-Smith I, Watkins H\", TITLE = \"Mutations in the gamma(2) subunit of AMP-activated protein kinase cause familial hypertrophic cardiomyopathy: evidence for the central role of energy compromise in disease pathogenesis.\", JOURNAL = c(\"Hum Mol Genet 10:1215-20 (2001)\", \"DOI:10.1093/hmg/10.11.1215\")), list(REFERENCE = \"PMID:11716909\", AUTHORS = \"Franz WM, Muller OJ, Katus HA\", TITLE = \"Cardiomyopathies: from genetics to the prospect of treatment.\", JOURNAL = c(\"Lancet 358:1627-37 (2001)\", \"DOI:10.1016/S0140-6736(01)06657-0\")), list(REFERENCE = \"PMID:18056765\", AUTHORS = \"Morimoto S\", TITLE = \"Sarcomeric proteins and inherited cardiomyopathies.\", JOURNAL = c(\"Cardiovasc Res 77:659-66 (2008)\", \"DOI:10.1093/cvr/cvm084\")), list(REFERENCE = \"PMID:19136482\", AUTHORS = \"Cambronero F, Marin F, Roldan V, Hernandez-Romero D, Valdes M, Lip GY\", TITLE = \"Biomarkers of pathophysiology in hypertrophic cardiomyopathy: implications for clinical management and prognosis.\", JOURNAL = c(\"Eur Heart J 30:139-51 (2009)\", \"DOI:10.1093/eurheartj/ehn538\")), list(REFERENCE = \"PMID:11805847\", AUTHORS = \"Towbin JA, Bowles NE\", TITLE = \"The failing heart.\", JOURNAL = c(\"Nature 415:227-33 (2002)\", \"DOI:10.1038/415227a\")), list(REFERENCE = \"PMID:11225594\", AUTHORS = \"Sorajja P, Elliott PM, McKenna WJ\", TITLE = \"The molecular genetics of hypertrophic cardiomyopathy: prognostic implications.\", JOURNAL = c(\"Europace 2:4-14 (2000)\", \"DOI:10.1053/eupc.1999.0067\")), list(REFERENCE = \"PMID:18572189\", AUTHORS = \"Landstrom AP, Parvatiyar MS, Pinto JR, Marquardt ML, Bos JM, Tester DJ, Ommen SR, Potter JD, Ackerman MJ\", TITLE = \"Molecular and functional characterization of novel hypertrophic cardiomyopathy susceptibility mutations in TNNC1-encoded troponin C.\", JOURNAL = c(\"J Mol Cell Cardiol 45:281-8 (2008)\", \"DOI:10.1016/j.yjmcc.2008.05.003\"))', 'map05410', 'Hypertrophic cardiomyopathy (HCM)', 65, 0, 65, 0, 0, 0, 0, 0);
INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (459, '05412', 'Arrhythmogenic right ventricular cardiomyopathy (ARVC)', 'Arrhythmogenic right ventricular cardiomyopathy (ARVC) is an inherited heart muscle disease that may result in arrhythmia, heart failure, and sudden death. The hallmark pathological findings are progressive myocyte loss and fibrofatty replacement, with a predilection for the right ventricle. A number of genetic studies have identified mutations in various components of the cardiac desmosome that have important roles in the pathogenesis of ARVC. Disruption of desmosomal function by defective proteins might lead to death of myocytes under mechanical stress. The myocardial injury may be accompanied by inflammation. Since regeneration of cardiac myocytes is limited, repair by fibrofatty replacement occurs. Several studies have implicated that desmosome dysfunction results in the delocalization and nuclear translocation of plakoglobin. As a result, competition between plakoglobin and beta-catenin will lead to the inhibition of Wnt/beta-catenin signaling, resulting in a shift from a myocyte fate towards an adipocyte fate of cells. The ryanodine receptor plays a crucial part in electromechanical coupling by control of release of calcium from the sarcoplasmic reticulum into the cytosol. Therefore, defects in this receptor could result in an imbalance of calcium homeostasis that might trigger cell death.', 'Human Diseases; Cardiovascular disease', 'Arrhythmogenic right ventricular cardiomyopathy (ARVC)', 'Arrhythmogenic right ventricular cardiomyopathy, Naxos disease, Brugada syndrome', 'ko05412', 'list(REFERENCE = \"PMID:19205777\", AUTHORS = \"Herren T, Gerber PA, Duru F\", TITLE = \"Arrhythmogenic right ventricular cardiomyopathy/dysplasia: a not so rare \\\"disease of the desmosome\\\" with multiple clinical presentations.\", JOURNAL = c(\"Clin Res Cardiol 98:141-58 (2009)\", \"DOI:10.1007/s00392-009-0751-4\")), list(REFERENCE = \"PMID:17413274\", AUTHORS = \"van Tintelen JP, Hofstra RM, Wiesfeld AC, van den Berg MP, Hauer RN, Jongbloed JD\", TITLE = \"Molecular genetics of arrhythmogenic right ventricular cardiomyopathy: emerging horizon?\", JOURNAL = c(\"Curr Opin Cardiol 22:185-92 (2007)\", \"DOI:10.1097/HCO.0b013e3280d942c4\")), list(REFERENCE = \"PMID:12270949\", AUTHORS = \"Fatkin D, Graham RM\", TITLE = \"Molecular mechanisms of inherited cardiomyopathies.\", JOURNAL = c(\"Physiol Rev 82:945-80 (2002)\", \"DOI:10.1152/physrev.00012.2002\")), list(REFERENCE = \"PMID:11716909\", AUTHORS = \"Franz WM, Muller OJ, Katus HA\", TITLE = \"Cardiomyopathies: from genetics to the prospect of treatment.\", JOURNAL = c(\"Lancet 358:1627-37 (2001)\", \"DOI:10.1016/S0140-6736(01)06657-0\")), list(REFERENCE = \"PMID:16823481\", AUTHORS = \"MacRae CA, Birchmeier W, Thierfelder L\", TITLE = \"Arrhythmogenic right ventricular cardiomyopathy: moving toward mechanism.\", JOURNAL = c(\"J Clin Invest 116:1825-8 (2006)\", \"DOI:10.1172/JCI29174\")), list(REFERENCE = \"PMID:18382419\", AUTHORS = \"Awad MM, Calkins H, Judge DP\", TITLE = \"Mechanisms of disease: molecular genetics of arrhythmogenic right ventricular dysplasia/cardiomyopathy.\", JOURNAL = c(\"Nat Clin Pract Cardiovasc Med 5:258-67 (2008)\", \"DOI:10.1038/ncpcardio1182\")), list(REFERENCE = \"PMID:16101641\", AUTHORS = \"Sen-Chowdhry S, Syrris P, McKenna WJ\", TITLE = \"Genetics of right ventricular cardiomyopathy.\", JOURNAL = c(\"J Cardiovasc Electrophysiol 16:927-35 (2005)\", \"DOI:10.1111/j.1540-8167.2005.40842.x\")), list(REFERENCE = \"PMID:16124859\", AUTHORS = \"Ahmad F, Seidman JG, Seidman CE\", TITLE = \"The genetic basis for cardiac remodeling.\", JOURNAL = c(\"Annu Rev Genomics Hum Gen