INSERT INTO `kegg-pathway-bottleneck`.`pathway_data` (`id`, `code`, `name`, `description`, `class`, `pathway_map`, `module`, `disease`, `dblinks`, `ko_pathway`, `reference`, `eukaryotes_count`, `prokaryotes_count`, `animals_count`, `plants_count`, `fungi_count`, `protists_count`, `bacteria_count`, `archaea_count`) VALUES (462, '05418', 'Fluid shear stress and atherosclerosis', 'Shear stress represents the frictional force that the flow of blood exerts at the endothelial surface of the vessel wall and plays a central role in vascular biology and contributes to the progress of atherosclerosis. Sustained laminar flow with high shear stress upregulates expressions of endothelial cell (EC) genes and proteins that are protective against atherosclerosis. The key shear stress-induced transcription factors that govern the expression of these genes are Kruppel-like factor 2 (KLF2) and nuclear factor erythroid 2-like 2 (Nrf2). On the other hand, disturbed flow with associated reciprocating, low shear stress generally upregulates the EC genes and proteins that promote oxidative and inflammatory states in the artery wall, resulting in atherogenesis. Important transcriptional events that reflect this condition of ECs in disturbed flow include the activation of activator protein 1 (AP-1) and nuclear factor kappaB (NF-kappaB).', 'Human Diseases; Cardiovascular disease', 'Fluid shear stress and atherosclerosis', 'ko05418', 'list(REFERENCE = \"PMID:21050140\", AUTHORS = \"Nigro P, Abe J, Berk BC\", TITLE = \"Flow shear stress and atherosclerosis: a matter of site specificity.\", JOURNAL = c(\"Antioxid Redox Signal 15:1405-14 (2011)\", \"DOI:10.1089/ars.2010.3679\")), list(REFERENCE = \"PMID:26597822\", AUTHORS = \"McSweeney SR, Warabi E, Siow RC\", TITLE = \"Nrf2 as an Endothelial Mechanosensitive Transcription Factor: Going With the Flow.\", JOURNAL = c(\"Hypertension 67:20-9 (2016)\", \"DOI:10.1161/HYPERTENSIONAHA.115.06146\")), list(REFERENCE = \"PMID:22076424\", AUTHORS = \"Heo K